import React, { useRef } from 'react';
import classes from './AddMovie.module.css';

function AddMovie(props) {

    const titleRef = useRef('');
    const overviewRef = useRef('');
    const releaseRef = useRef('');

    const titleInput = titleRef.current.value;
    const overviewInput = overviewRef.current.value;
    const releaseInput = overviewRef.current.value;

    const movieSubmitHandler = (event) => {
        event.preventDefault();

        const enteredData = {
            title: titleInput,
            openingText: overviewInput,
            releaseDate: releaseInput
        }

        props.onAddMovie(enteredData);
    }

  return (
    <form onSubmit={movieSubmitHandler}>
        <div className={classes.control}>
            <label htmlFor='title'>Title</label>
            <input type="text" id='title' ref={titleRef} /> 
        </div>
        <div className={classes.control}>
            <label htmlFor='opening-text'>Overview</label>
            <textarea id='opening-text' type="text" ref={overviewRef} />
        </div>
        <div className={classes.control}>
            <label htmlFor='date'>Release Date</label>
            <input type="text" id='date' ref={releaseRef} />
        </div>
        <div >
            <button type='submit'>Add Movie</button>
        </div>
    </form>
  )
}

export default AddMovie
