import React, { useEffect, useState, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";
import AddMovie from "./components/AddMovie";

function App() {
  const dummyMovies = [
    {
      id: 1,
      title: "Game Of Thrones",
      openingText: "Nine noble families fought to win the Iron Throne.",
      releaseDate: "2011-05-18",
    },
    {
      id: 2,
      title: "Stranger Things",
      openingText: "This is the second opening text of the movie",
      releaseDate: "2016-05-19",
    },
  ];

  const [movies, setMovies] = useState(dummyMovies);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const api_key =
    "https://api.themoviedb.org/3/tv/popular?api_key=de9929f37ff02f5324c8210f01ea2eff";
  //const api = "https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=de9929f37ff02f5324c8210f01ea2eff"

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(api_key);

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const transformedMovies = data.results.map((dataMovie) => {
        return {
          id: dataMovie.id,
          title: dataMovie.name,
          openingText: dataMovie.overview,
          releaseDate: dataMovie.first_air_data,
        };
      });
      setMovies(transformedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, [])

  useEffect(() => {
    fetchMoviesHandler()
  }, [fetchMoviesHandler])

  const addMovieHandler = (movie) => {
    setMovies((prevMovies) => {
      return [movie, prevMovies]
    })
  }

  return (
    <React.Fragment>
      <section>
      <AddMovie onAddMovie={addMovieHandler} /> 
      </section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && !error && <p>Found No Movies!</p>}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
